package jala.Corejava.arrays2;

public class Arrays41 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
        int[] x = {10,20,30};
        int total=0;
        for (int i=0;i<x.length;i++) {
        	total+=x[i];
        }
        	System.out.println("The Sum is : "+ total);
        }

}
